const crypto = require('crypto');
const fs = require('fs');

const sa = JSON.parse(fs.readFileSync('/credentials/gcp-key.json', 'utf8'));

const header = Buffer.from(JSON.stringify({
  alg: 'RS256',
  typ: 'JWT'
})).toString('base64url');

const now = Math.floor(Date.now() / 1000);
const claim = Buffer.from(JSON.stringify({
  iss: sa.client_email,
  scope: 'https://www.googleapis.com/auth/cloud-platform',
  aud: 'https://oauth2.googleapis.com/token',
  iat: now,
  exp: now + 3600
})).toString('base64url');

const sign = crypto.createSign('RSA-SHA256');
sign.update(`${header}.${claim}`);
const signature = sign.sign(sa.private_key, 'base64url');

const jwt = `${header}.${claim}.${signature}`;

return { json: { ...$input.first().json, gcpJwt: jwt } };

const tokenResponse = $input.first().json;
const prevData = $('Generate GCP JWT').first().json;
return { json: { ...prevData, gcpAccessToken: tokenResponse.access_token } };

