const response = $input.first().json;
const text = response.candidates?.[0]?.content?.parts?.[0]?.text || '{}';

let jsonStr = text;
jsonStr = jsonStr.replace(/```json\n?/g, '').replace(/```\n?/g, '');

const firstBrace = jsonStr.indexOf('{');
const lastBrace = jsonStr.lastIndexOf('}');
if (firstBrace !== -1 && lastBrace !== -1 && lastBrace > firstBrace) {
  jsonStr = jsonStr.substring(firstBrace, lastBrace + 1);
}

try {
  const parsed = JSON.parse(jsonStr);
  const topics = parsed.topics || [];
  const bestTopic = topics.sort((a, b) => 
    (b.viralScore + b.educationalScore) - (a.viralScore + a.educationalScore)
  )[0];

  if (!bestTopic) throw new Error('No topic found');

  return { json: { ...$input.first().json, selectedTopic: bestTopic, allTopics: topics } };
} catch (e) {
  throw new Error(`Topic parsing failed: ${e.message}\nRaw: ${text.substring(0, 200)}...`);
}

