const response = $input.first().json;
const text = response.candidates?.[0]?.content?.parts?.[0]?.text || '{}';

let jsonStr = text;
jsonStr = jsonStr.replace(/```json\n?/g, '').replace(/```\n?/g, '');

const firstBrace = jsonStr.indexOf('{');
const lastBrace = jsonStr.lastIndexOf('}');
if (firstBrace !== -1 && lastBrace !== -1 && lastBrace > firstBrace) {
  jsonStr = jsonStr.substring(firstBrace, lastBrace + 1);
}

try {
  const parsed = JSON.parse(jsonStr);
  if (!parsed.script || !parsed.quiz || !parsed.qualityCheck) throw new Error('Missing required fields');

  return {
    json: {
      ...$input.first().json,
      script: parsed.script,
      quiz: parsed.quiz,
      qualityCheck: parsed.qualityCheck,
      totalDuration: parsed.script.totalDuration || 60
    }
  };
} catch (e) {
  throw new Error(`Script parsing failed: ${e.message}\nRaw: ${text.substring(0, 200)}...`);
}

const prevContext = $input.first().json;
const sections = prevContext.script.sections || [];
const accessToken = $('Merge Token').first().json.gcpAccessToken;
const projectId = $env.GCP_PROJECT_ID;
const location = $env.VERTEX_AI_LOCATION;

const url = `https://${location}-aiplatform.googleapis.com/v1/projects/${projectId}/locations/${location}/publishers/google/models/imagen-3.0-generate-002:predict`;

const generatedImages = [];

for (let i = 0; i < sections.length; i++) {
  const section = sections[i];
  const visualCue = section.visualCue || `Educational image about ${prevContext.category}`;

  // Build realistic image prompt based on visualCue
  const prompt = `Professional educational photograph: ${visualCue}. ` +
    `High-quality, photorealistic style. ` +
    `Clean composition, professional lighting. ` +
    `Suitable for educational video content. ` +
    `Vertical 9:16 format. ` +
    `NO cartoon, NO illustration, NO anime style. ` +
    `NO text, NO watermarks. ` +
    `Realistic and professional appearance.`;

  const response = await fetch(url, {
    method: 'POST',
    headers: {
      'Authorization': `Bearer ${accessToken}`,
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      instances: [{ prompt }],
      parameters: {
        sampleCount: 1,
        aspectRatio: '9:16',
        safetyFilterLevel: 'block_only_high',
        personGeneration: 'allow_adult'
      }
    })
  });

  const data = await response.json();
  const imageBase64 = data.predictions?.[0]?.bytesBase64Encoded;

  if (!imageBase64) {
    throw new Error(`Image generation failed for section ${i}: ${JSON.stringify(data).substring(0, 300)}`);
  }

  generatedImages.push({
    sectionIndex: i,
    sectionType: section.type,
    startTime: section.startTime,
    endTime: section.endTime,
    duration: section.endTime - section.startTime,
    imageBase64
  });
}

return {
  json: {
    ...prevContext,
    generatedImages
  }
};

const prevContext = $('4a. Visual Gen (Multi-Section)').first().json;
const generatedImages = prevContext.generatedImages || [];

// Create binary data for each image
const binaries = {};
for (let i = 0; i < generatedImages.length; i++) {
  const img = generatedImages[i];
  binaries[`image_${i}`] = {
    data: img.imageBase64,
    mimeType: 'image/png',
    fileName: `section_${i}.png`
  };
}

return {
  json: prevContext,
  binary: binaries
};

const item = $input.first();
const prevContext = $('4a. Visual Gen (Multi-Section)').first().json;
const audioBase64 = item.json.audioContent;

if (!audioBase64) throw new Error('TTS response missing audioContent: ' + JSON.stringify(item.json).substring(0, 500));

return {
  json: { ...prevContext },
  binary: {
    audio: {
      data: audioBase64,
      mimeType: 'audio/mp3',
      fileName: 'narration.mp3'
    }
  }
};

const items = $input.all();
const visualItem = items.find(i => i.binary?.image_0);
const audioItem = items.find(i => i.binary?.audio);

const jsonData = visualItem?.json || audioItem?.json || {};

let combinedBinary = {};
for (const item of items) {
  if (item.binary) {
    combinedBinary = { ...combinedBinary, ...item.binary };
  }
}

return { json: jsonData, binary: combinedBinary };

const JSZip = require('jszip');

const item = $input.first();
const zipBinary = item.binary?.data;

if (!zipBinary) throw new Error('No ZIP response');

const zipBuffer = Buffer.from(zipBinary.data, 'base64');
const zip = await JSZip.loadAsync(zipBuffer);

const videoFile = zip.file('video.mp4');
if (!videoFile) throw new Error('video.mp4 not found in ZIP');
const videoBase64 = await videoFile.async('base64');

const thumbnailFile = zip.file('thumbnail.jpg');
if (!thumbnailFile) throw new Error('thumbnail.jpg not found in ZIP');
const thumbnailBase64 = await thumbnailFile.async('base64');

const prevContext = $('Combine Binaries').first().json;

return {
  json: { ...prevContext, composedAt: new Date().toISOString() },
  binary: {
    video: { data: videoBase64, mimeType: 'video/mp4', fileName: 'final.mp4' },
    thumbnail: { data: thumbnailBase64, mimeType: 'image/jpeg', fileName: 'thumbnail.jpg' }
  }
};

const items = $input.all();
const prevContext = $('Unzip Response').first().json;

const s3Region = $env.AWS_REGION || 'ap-northeast-2';
const s3Bucket = $env.S3_BUCKET;

const videoUrl = `https://${s3Bucket}.s3.${s3Region}.amazonaws.com/generated/${prevContext.jobId}/final.mp4`;
const thumbnailUrl = `https://${s3Bucket}.s3.${s3Region}.amazonaws.com/generated/${prevContext.jobId}/thumbnail.jpg`;

return {
  json: {
    ...prevContext,
    videoUrl,
    thumbnailUrl,
    s3Key: `generated/${prevContext.jobId}/final.mp4`,
    thumbnailS3Key: `generated/${prevContext.jobId}/thumbnail.jpg`
  }
};

